using Chess_App.Classes;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;

namespace Chess_App
{
    public class Guest
    {
        public int ID { get; set; }
        public string Username { get; set; }
        public Theme Theme { get; set; }
        public byte[] ProfilePicture { get; set; }
        public string ProfilePictureString { get; set; } 
        public Guest()
        {
            Username = "Guest";
            Theme = new Theme("#FFFFFF", "#000000", "#3B3B3B");
            string hexString = "";
            byte[] ImageBytes = new byte[hexString.Length / 2];
            for (int i = 0; i < hexString.Length; i += 2)
            {
                ImageBytes[i / 2] = Convert.ToByte(hexString.Substring(i, 2), 16);
            }
            ProfilePicture = ImageBytes;
            ProfilePictureString = PlayerAccount.GetImgSrc(ImageBytes);
        }
    }
}